--
-- Table structure for table `tb_rule`
--

DROP TABLE IF EXISTS `tb_rule`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `tb_rule` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL,
  `author` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL,
  `read_num` int(11) NOT NULL,
  `text` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `content` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_time` datetime(6) NOT NULL,
  `rule_type_id` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `tb_rule_rule_type_id_4c01d240_fk_tb_rule_type_id` (`rule_type_id`),
  CONSTRAINT `tb_rule_rule_type_id_4c01d240_fk_tb_rule_type_id` FOREIGN KEY (`rule_type_id`) REFERENCES `tb_rule_type` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

